pragma solidity ^0.5.0;

import "./ZombieFeeding.sol";

contract ZombieHelper is ZombieFeeding {

  uint levelUpFee = 0.001 ether;
  mapping (address => uint) pendingWithdrawals;

  modifier aboveLevel(uint _level, uint _zombieId) {
    require(zombies[_zombieId].level >= _level);
    _;
  }

  // can only send to payable account, owner() not payable
  // used withdraw common pattern instead.
  function withdraw() public onlyOwner {
    uint amount = pendingWithdrawals[msg.sender];
    pendingWithdrawals[msg.sender] = 0;
    msg.sender.transfer(amount);
  }

  function setLevelUpFee(uint _fee) external onlyOwner {
    levelUpFee = _fee;
  }

  function levelUp(uint _zombieId) external payable {
    require(msg.value == levelUpFee);
    pendingWithdrawals[address(this)] += msg.value;
    zombies[_zombieId].level = zombies[_zombieId].level.add(1);
  }

  function changeName(uint _zombieId, string calldata _newName)
    external
    aboveLevel(2, _zombieId)
    ownerOfZombie(_zombieId)
  {
    zombies[_zombieId].name = _newName;
  }

  function changeDna(uint _zombieId, uint _newDna)
    external
    aboveLevel(20, _zombieId)
    ownerOfZombie(_zombieId)
  {
    require(msg.sender == zombieToOwner[_zombieId]);
    zombies[_zombieId].dna = _newDna;
  }

  function getZombiesByOwner(address _owner) external view returns (uint[] memory) {
    uint[] memory result = new uint[](ownerZombieCount[_owner]);
    uint counter = 0;
    for (uint i = 0; i < zombies.length; i++) {
      if (zombieToOwner[i] == _owner) {
        result[counter] = i;
        counter = counter.add(1);
      }
    }
    return result;
  }
}
